class SeedController extends Controller
{
    public function actionReset()
    {
		d('reset');
        $connection = Yii::$app->db;
        $connection->createCommand("SET FOREIGN_KEY_CHECKS = 0")->execute();
        foreach ($connection->getSchema()->getTableNames() as $tableName)
        {
            if ('migration' === $tableName)
                continue;

            $connection->createCommand("TRUNCATE TABLE `$tableName`")->execute();
        }
        $connection->createCommand("SET FOREIGN_KEY_CHECKS = 1")->execute();
    }

    public function actionIndex()
    {
		d('seed');
        $info = $this->_info();
        SiteInfo::saveData($info);
        $connection = Yii::$app->db;
        $transaction = $connection->beginTransaction();
        try
        {
    		d('Currency');
            if (!Currency::find()->exists())
            {
                foreach ($this->_currencies() as $currencyData)
                {
                    $currency = new Currency([
                        'id'    => $currencyData[0],
                        'name'  => $currencyData[1],
                        'code'  => $currencyData[2],
                        'rate'  => $currencyData[3],
                    ]);
                    $currency->save();
                }
            }

            d('Country');
            if (!Country::find()->exists())
            {
                foreach ($this->_countries() as $countryData)
                {
                    $country = new Country([
                        'name_uk'   => $countryData[0],
                        'name_ru'   => $countryData[0],
                        'iso3'      => $countryData[1],
                        'iso2'      => $countryData[2],
                    ]);
                    $country->save();
                }
            }

            d('OrderStatus');
            if (!OrderStatus::find()->exists())
            {
                foreach ($this->_order_statuses() as $statusData)
                {
                    $orderStatus = new OrderStatus([
                        'name_uk'   => $statusData['uk'],
                        'name_ru'   => $statusData['ru'],
                    ]);
                    $orderStatus->save();
                }
            }

            d('PaymentSystem');
            if (!PaymentSystem::find()->exists())
            {
                foreach ($this->_payment_systems() as $paymentSystemData)
                {
                    $paymentSystem = new PaymentSystem([
                        'name_uk'   => $paymentSystemData['uk'],
                        'name_ru'   => $paymentSystemData['ru'],
                    ]);
                    $paymentSystem->save();
                }
            }

            d('User');
            $admin = new User([
                'login' => 'admin',
                'email' => 'admin@hectare.com.ua',
                'is_admin' => 1,
            ]);
            $admin->setPassword('admin');
            $admin->generateAuthKey();
            if (!$admin->save())
                _d($admin->getErrors());

            d('Manager');
            $manager = new Manager([
              'name' => 'manager',
              'phone' => '380987654321'
            ]);
            $image = new Image([
              'url' => 'https://cdn4.iconfinder.com/data/icons/rcons-user/32/boss_man-512.png'
            ]);
            $image->save();
            $manager->link('image', $image);

            $transaction->commit();
        }
        catch (\Exception $e)
        {
            $transaction->rollBack();
            throw $e;
        }
    }

