(114, 238, 'З Днем Незалежності України!', 'z-dnem-nezalezhnosti-ukrajini', '<p style="text-align: center;">Корпорація Гектар вітає всіх з Днем Незалежності України! </p>\r\n<p style="text-align: center;">Бажаємо вам щедрого врожаю, міцного здоров''я, мирного неба, щастя, добра і злагоди, творчого натхнення і плідної роботи.</p>\r\n<p style="text-align: center;">Нехай кожен день ваш буде наповнений теплом, радістю, і новими досягненнями, а ваші добрі справи примножать славу рідної України.</p>\r\n<p style="text-align: center;"> </p>', '', 1, 9, '2016-08-23 10:41:23', 545, '', '2016-08-23 10:52:41', 545, 0, '0000-00-00 00:00:00', '2016-08-23 10:42:00', '0000-00-00 00:00:00', '{"image_intro":"images\\/2016-08_баннер_на_сайт-01.jpg","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 7, 0, '', '', 1, 39, '{"robots":"","author":"","rights":"","xreference":""}', 0, 'uk-UA', '');

